import { Component } from "react";
import { Comments } from "./Comments";
import { PostContext } from "../Context";
import { Link } from "react-router-dom";

export class TopicDis extends Component {
    state = {
        // Post
        ID: "",
        Title: "",
        Content: "",
        Author: "",
        PostError: "",
        Loading: false,
        // Comments
        CommentsList: [],
        Total: 0,
        PageSize: 0,
        Page: 1,
        Loading: false,
        ComError: "",
    }

    static contextType = PostContext

    componentDidMount() {
        this.updateData()
        this.fetchData()
    }

    componentWillUnmount() {
        const {setPosID, setTitle, setContent, setAuthor} = this.context
        setPosID("")
        setTitle("")
        setContent("")
        setAuthor("")
    }

    updateData = () => {
            console.log(this.context)
            this.setState({
                ID: this.context.PostID,
                Title: this.context.PostTitle,
                Author: this.context.PostAuthor,
                Content: this.context.PostContent,
            })
    }

    fetchData = async () => {
        const {Page, ID} = this.state
        try {
            const response = await fetch(`http://localhost:9091/getPosts?page=${Page}&post_id=${ID}`, {
                method: 'GET', 
                headers: {
                    'Content-Type': 'application/json', 
                },
            })
            const result = await response.json()
            if (result.error !== "") {
                this.setState({ComError : result.error})
            } else {
                this.setState({
                    Comments: result.posts,
                    Total: result.total,
                    PageSize: result.page_size,
                    Page: result.page,
                    ComError: ""
                })
            }
            console.log("Ответ от API:", result);
            } catch (error) {
                console.error("Ошибка:", error);
            }
    }

    componentDidMount() {
        this.fetchData()
    }

    render() {
            const {Content, Loading, PostError, ComError, Title, Author, CommentsList} = this.state;

        if (Loading) {
            return <p>Загрузка...</p>;
        }

        if (PostError) {
            return <p>Ошибка: {PostError}</p>;
        }
        if (ComError) {
            return <p>Ошибка: {ComError}</p>;
        }

        return (
            <>
                <h1>{Title}</h1>
                <div className="text">
                    {Content}
                </div>
                <div>
                    {Author}
                </div>
                <button>Добавить коментарий</button>
                <Comments data={CommentsList}/>
                <Link to="/forum"><button>Назад</button></Link>
            </>
        )
    }
}
