import { Component } from "react";

export class SignUp extends Component {
    state = {
        login: "",
        password: "",
        name: "",
        errorKey: null
    }

    UpdateData = (e) => { 
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
    }

    LogButtonClicked = () => {
        console.log(`Логин: ${this.state.login}`)
        console.log(`Пароль: ${this.state.password}`)
        console.log(`Имя игрока: ${this.state.name}`)

        if (this.state.login === "" || this.state.password === "" || this.state.name === "") {
            this.setState({errorKey: 0})
            return
        }
        if (this.state.login.includes(" ") === true || this.state.password.includes(" ") === true
            || this.state.name.includes(" ") === true)

        this.setState ({
            login: "",
            password: "",
            name: ""
        })
    }

    render () {
        const { login, password, name} = this.state
        return (
            <>
                <label>Регистрация</label>
                <div>
                    <label>логин</label>
                    <input name="login" value={login} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <div>
                    <label>пароль</label>
                    <input type="password" name="password" value={password} onChange={(e) => this.UpdateData(e)}/>
                    <button>Показать</button>
                </div>
                <div>
                    <label>имя игрока/никнейм</label>
                    <input name="name" value={name} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <button onClick={this.LogButtonClicked}>Зарегистироваться</button>
            </>
        )
    }
}
