import { Component } from "react";

export class SignUp extends Component {
    state = {
        login: "kk",
        password: "",
        name: ""
    }

    UpdateData = (event) => {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    LogButtonClicked = () => {
        console.log(this.state.login)
        console.log(this.state.password)
        console.log(this.state.name)
        this.setState ({
            login: "",
            password: "",
            name: ""
        })
    }

    render () {
        const { login, password, name} = this.state
        return (
            <>
                <div>
                    <label>логин</label>
                    <input name="логин" value={login} onChange={this.UpdateData}/>
                </div>
                <div>
                    <label>пароль</label>
                    <input type="password" name="password" value={password} onChange={this.UpdateData}/>
                </div>
                <div>
                    <label>имя игрока/никнейм</label>
                    <input name="name" value={name} onChange={this.UpdateData}/>
                </div>
                <button onClick={this.LogButtonClicked}>Зарегистироваться</button>
                <label>{login}</label>
            </>
        )
    }
}
