import { createBrowserRouter, RouterProvider} from 'react-router-dom';
import { Panel } from "./Panel";
import { Field } from "./PlayField";
import { SignUp } from './sign_up';
import { AppContext } from './Context';
import {useState } from 'react';


const router = createBrowserRouter([
  {
    path: '/',
    element: <Panel/>,
    children: [
      {
        path: '/',
        element: <Field/>
      },
      {
        path: '/game_rules',
        //element: <Rules/>
        element: <></>
      },
      {
        path: '/sign_up',
        element: <SignUp/>
      },
      {
        path: '/sign_in',
        //element: <SignIn/>
        element: <></>
      },
      {
        path: '/game',
        //element: <PlayField/>
        element: <></>
      },
      {
        path: '/forum',
        //element: <Forum/>
        element: <></>
      },
      {
        path: '/profile',
        element: <></>
      }
    ]
  }
])

function App() {
  const [UserIsLoged, setUserIsLoged] = useState(false)
  const [UserName, setUserName] = useState("nameless")
  const [Avatar, setAvatar] = useState("./images/avatar24.png")

  const login = () => {
    setUserIsLoged(true)
  }

  const logout = () => {
    setUserIsLoged(false)
  }

  return (
  <AppContext.Provider value={{
    UserIsLoged, UserName, Avatar, login, logout
  }}>
    <div className="App">
      <RouterProvider router={router}></RouterProvider>
      </div>
      </AppContext.Provider>
  )
}

export default App;
