import { Component } from 'react';
import io from 'socket.io-client';
//import { Square } from './Square'; // Предполагается, что компонент Square в отдельном файле

export class Square extends Component {
    render() {
        const { value, onClick } = this.props;

        return (
            <button
                style={{
                    width: '60px',
                    height: '60px',
                    fontSize: '24px',
                    display: 'inline-block',
                    border: '1px solid black',
                    backgroundColor: 'white',
                    cursor: 'pointer',
                }}
                onClick={onClick} // Вызов onClick при клике
            >
                {value} {/* Отображение X или O, если клетка занята */}
            </button>
        );
    }
}

export class Board extends Component {
    state = {
        Board: Array(9).fill(null), // Начальное состояние игрового поля
        NextStep: true, // Ход X или O
        Scores: { X: 0, O: 0 }, // Счёт
    };

    componentDidMount() {
        // Подключение к серверу через socket.io
        this.socket = io('http://localhost:4000');

        // Получаем обновленное состояние игры от сервера
        this.socket.on('gameState', (gameState) => {
            this.setState({
                Board: gameState.Board,
                NextStep: gameState.NextStep,
                Scores: gameState.Scores,
            });
        });
    }

    componentWillUnmount() {
        // Отключаемся от сервера при размонтировании компонента
        if (this.socket) {
            this.socket.disconnect();
        }
    }

    // Метод для обработки хода
    SClick = (index) => {
        const { Board, NextStep } = this.state;
        if (Board[index]) return; // Если клетка занята, не делаем ход

        // Отправляем событие на сервер с информацией о ходе
        this.socket.emit('makeMove', { index });

        // Локальное обновление состояния (хотя сервер тоже передаст это состояние)
        const newBoard = [...Board];
        newBoard[index] = NextStep ? 'X' : 'O';
        this.setState({
            Board: newBoard,
            NextStep: !NextStep,
        });
    };

    render() {
        const { Board, NextStep, Scores } = this.state;

        return (
            <div>
                <div>Следующий ход: {NextStep ? 'X' : 'O'}</div>
                <div>Счёт: X - {Scores.X}, O - {Scores.O}</div>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px' }}>
                    {Board.map((square, index) => (
                        <Square
                            key={index}
                            value={square}
                            onClick={() => this.SClick(index)} // Отправка хода на сервер
                        />
                    ))}
                </div>
            </div>
        );
    }
}

