import { Component } from "react";
import io from 'socket.io-client';

export class Square extends Component {
    render() {
        return (
            <button
                style={{
                    width: '60px',
                    height: '60px',
                    fontSize: '24px',
                    display: 'inline-block',
                    border: '1px solid black',
                    backgroundColor: 'white',
                }}
                onClick={this.props.onClick}>
                {this.props.value}
            </button>
        );
    }
}

export class Board extends Component {
    state = {
        Board: Array(9).fill(null).map(() => Array(9).fill(null)), // 9x9 сетка
        NextStep: true,
        Scores: { X: 0, O: 0 },
        winner: null,
    };

    componentDidMount() {
        this.socket = io('http://localhost:4000');

        this.socket.on('gameState', (gameState) => {
            this.setState({
                Board: gameState.Board,
                NextStep: gameState.NextStep,
                Scores: gameState.Scores,
                winner: gameState.winner,
            });
        });

        this.socket.on('gameOver', (finalScores) => {
            alert(`Игра завершена! Победители X - ${finalScores.X} O - ${finalScores.O}`);
        });
    }

    componentWillUnmount() {
        if (this.socket) {
            this.socket.disconnect();
        }
    }

    // Обработчик клика на клетку
    SClick = (rowIndex, colIndex) => {
        const { Board, NextStep, winner } = this.state;

        if (Board[rowIndex][colIndex] || winner) {
            return; // Клетка уже занята или игра завершена
        }

        // Изменить клетку в сетке
        Board[rowIndex][colIndex] = NextStep ? 'X' : 'O';

        // Отправить ход на сервер
        this.socket.emit('makeMove', { rowIndex, colIndex });

        // Изменить шаг для следующего игрока
        this.setState({ NextStep: !NextStep });
    };

    // Отображаем каждую клетку
    RenderSquare = (rowIndex, colIndex) => {
        const { Board } = this.state;
        return (
            <Square
                key={`${rowIndex}-${colIndex}`}
                value={Board[rowIndex][colIndex]}
                onClick={() => this.SClick(rowIndex, colIndex)}
            />
        );
    };

    render() {
        const { Scores, NextStep, winner } = this.state;
        const status = `Следующий ход: ${NextStep ? "X" : "O"}`;
        const scoresText = `Счёт: X - ${Scores.X}, O - ${Scores.O}`;
        const gameStatus = winner ? `${winner} победил!` : status;

        return (
            <div>
                <div style={{ marginBottom: '10px' }}>{gameStatus}</div>
                <div>{scoresText}</div>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(9, 1fr)', gap: '1px' }}>
                    {/* Рендерим сетку 9x9 */}
                    {Array.from({ length: 9 }, (_, rowIndex) => (
                        Array.from({ length: 9 }, (_, colIndex) => (
                            <div key={`${rowIndex}-${colIndex}`} style={{ display: 'flex', justifyContent: 'center' }}>
                                {this.RenderSquare(rowIndex, colIndex)}
                            </div>
                        ))
                    ))}
                </div>
            </div>
        );
    }
}

export class Game extends Component {
    render() {
        return (
            <>
                <div className="field_position">
                    <div style={{ textAlign: "center", marginTop: "20px" }}></div>
                </div>
                <Board />
            </>
        );
    }
}
