import { Component } from "react"
import { AppContext } from "../App/Context"
import { Link } from "react-router-dom"

export class NewTopic extends Component {
    static contextType = AppContext
    state = {
        Title: "",
        Content: "",
        Author: this.context.Login,
        errorKey: null,
        ShowWarning: false,  // 🔔 Показывать предупреждение
        ToBack: false,       // ⬅️ Флаг для перехода назад
        TempTitle: "",
        TempContent: ""
    }

    Update = (e) => { 
        const {name, value} = e.target
        this.setState({
            [name]: value,
            errorKey: null
        })
    }

    CreateTopic = async (postData) => {
        const jsonData = JSON.stringify(postData);
        try {
            const response = await fetch("http://localhost:9091/createPost", {
                method: 'POST', 
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${this.context.UserToken}`
                },
                body: jsonData
            })
            if (response.ok) {
                console.log("Пост успешно создан!")
            }
            const result = await response.json()
            console.log("Ответ от API:", result);
        } catch (error) {
            console.error("Ошибка:", error);
        }
    }

    CreateTopicButton = () => {
        const {Title, Content, Author} = this.state

        if (Title === "") {
            this.setState({errorKey: 1})
            return
        }
        if (Content === "") {
            this.setState({errorKey: 2})
            return
        }
        if (Title.trim() !== Title) {
            this.setState({errorKey: 3})
            return
        }

        const data = {
            title: Title,
            content: Content,
            login: Author
        }

        this.CreateTopic(data)

        this.setState({
            Title: "",
            Content: "",
            errorKey: 0
        })
    }

    ToBackButton = () => {
        const {Title, Content} = this.state
        if (Title.trim() !== "" || Content.trim() !== "") {
            this.setState({
                ShowWarning: true,
                TempTitle: Title,
                TempContent: Content,
                ToBack: true
            })
        } else {
            window.location.href = "/forum"
        }
    }

    confirmHideFields = () => {
        this.setState({
            Title: "",
            Content: "",
            ShowWarning: false,
            ToBack: false
        }, () => {
            if (this.state.ToBack) {
                window.location.href = "/forum"
            }
        })
    }

    cancelHideFields = () => {
        this.setState({
            ShowWarning: false
        })
    }

    render() {
        return (
            <>
                <h1>Создание нового обсуждения</h1>
                <div>
                    <p>Название обсуждения</p>
                    <input 
                        type="text" 
                        name="Title" 
                        onChange={this.Update}
                        value={this.state.Title}
                    />
                </div>
                <div>
                    <p>Описание</p>
                    <textarea 
                        id="large-text"
                        name="Content"
                        onChange={this.Update}
                        placeholder="Тут можно написать о своих идеях или других предложениях"
                        rows={10} 
                        cols={45}
                        style={{ width: '75%', minHeight: '150px', padding: '10px', border: '1px solid #ccc', borderRadius: '4px', fontSize: '16px', lineHeight: '1.5', margin: "10px" }}
                        value={this.state.Content}
                    />
                </div>
                <div>
                    <button onClick={this.CreateTopicButton}>Создать</button>
                </div>
                {this.state.errorKey === 0 && <p>Новое обсуждение успешно создано!</p>}
                {this.state.errorKey === 1 && <p>Поле с названием обсуждения оказалось пустым. Пожалуйста, заполните его!</p>}
                {this.state.errorKey === 2 && <p>Поле с текстом обсуждения оказалось пустым. Пожалуйста, заполните его!</p>}
                {this.state.errorKey === 3 && <p>В начале или конце названия обсуждения есть пробелы. Пожалуйста, уберите их!</p>}
                <button onClick={this.ToBackButton}>Назад</button>

                {this.state.ShowWarning && (
                    <div className="background">
                        <div className="warning">
                            <p>Несохранённые данные будут потеряны. Продолжить?</p>
                            <button onClick={this.confirmHideFields} style={{ marginRight: '10px' }}>Да</button>
                            <button onClick={this.cancelHideFields}>Нет</button>
                        </div>
                    </div>
                )}
            </>
        )
    }
}
