import { Component } from "react"

export class Square extends Component {
    render() {
        return (
            <button
            style={{
                width: '60px',
                height: '60px',
                fontSize: '24px',
                display: 'inline-block',
                border: '1px solid black',
                backgroundColor: 'white', 
            }}>
                {this.props.value}
            </button>
        )
    }
}

export class Board extends Component {
    state = {
        //Size: 9,
        Squares: Array(9).fill(null),
        //Squares: Array(9).fill(null),
        //WinSquares : Array(9).fill(false),
        NextStep: true
        //NextBoard: null
    }

    IsEndGame(squares) {
        const lines = [
            [0, 1, 2],
            [3, 4, 5],
            [6, 7, 8],
            [0, 3, 6],
            [1, 4, 7],
            [2, 5, 8],
            [0, 4, 8],
            [2, 4, 6],
        ]

        //for (let i = 0; i < 9; i++) {
            for (let line of lines) {
                const [a, b, c] = line
                if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
                    //this.state.WinSquares[i] = true
                    return squares[a]
                }
        //    }
        }

        /*
        let WinKey = false
        let count = 0
        for (let i = 0; i < 9; i++) {
            if (this.state.WinSquares[i] == true) {
                count++
            }
        }
        if (count == 9) {
            WinKey = true
        }
        */

        //return WinKey
        return null
    }

    RenderSquare(index) {
        return (
            <Square
            //key={index}
            value={this.state.Squares[index]}
            onClick={() => this.SClick(index)}
            />
        )
    }

    SClick(index) {
        const squares = [...this.state.Squares]

        if (squares[index] || this.IsEndGame(squares)) {
            return
        }

        squares[index] = this.state.NextStep ? "X" : "O"
        this.setState({
            squares: squares,
            NextStep: !this.state.NextStep
        })
    }
    render() {
        const winner = this.IsEndGame(this.state.Squares)
        const status = winner ? `Победитель: ${winner}` : `Следующий ход: ${this.state.NextStep ? "X" : "O"}`
        //const board = Array.from({length:9}, (_, row) => (
        //    <div key={row} style={{display: "flex"}}>
        //        {Array.from({length:9}, (_, col) => this.RenderSquare(row * 9 + col))}
        //    </div>
        //))

        return (
            /*
            <div>
                <div>{status}</div>
                <div>{board}</div>
            </div>
            */

            <div>
                <div style={{marginBottom: "10px"}}>{status}</div>
                <div style={{display: "flex", flexDirection: "column"}}>
                    <div style={{display: "flex"}}>
                        {this.RenderSquare(0)}
                        {this.RenderSquare(1)}
                        {this.RenderSquare(2)}
                    </div>
                    <div style={{display: "flex"}}>
                        {this.RenderSquare(3)}
                        {this.RenderSquare(4)}
                        {this.RenderSquare(5)}
                    </div>
                    <div style={{display: "flex"}}>
                        {this.RenderSquare(6)}
                        {this.RenderSquare(7)}
                        {this.RenderSquare(8)}
                    </div>
                </div>
            </div>
        )
    }
}

export class Game extends Component {
    //static contextType = AppContext
    render() {
        return (
            <>
                <div className="field_position">
                <field style= {{
                    textAlign: "center",
                    marginTop: "20px"
                }}><Board/></field>
                <chat></chat>
                </div>
                <Board/>
            </>
        )
    }
}
