import { Component } from "react";
import { AppContext } from "../App/Context";
import {} from "./Comments.css"

export class Comment extends Component {
    static contextType = AppContext
    state = {
        Error: ""
    }

    DeleteComment = async (id) => {
        try {
            const response = await fetch(`http://localhost:9091/deleteCommentById?comment_id=${id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type' : 'application/json',
                    'Authorization' : `Bearer ${this.context.UserToken}`
                }
            })
            const result = await response.json()
            if (result.error !== "") {
                this.setState({Error : result.error})
            } else {
                this.setState({
                })
            }
            console.log("Ответ от API: ", result)
        }
        catch(error) {
            console.error("Ошибка: ", error)
        }
    }

    DeleteCommentButton = (id) => {
        console.log(id)
        this.DeleteComment(id)
    }

    render() {
        const {comment, id} = this.props
        return(
            <>
                <p>{comment.content}</p>
                <p>Автор: {comment.login}</p>
                {comment.login === this.context.Login && 
                    <button onClick={() => {this.DeleteCommentButton(id)}}>
                        Удалить комментарий
                    </button>
                }
            </>
        )
    }
}

export class Comments extends Component {
    render() {
        const {data} = this.props
        return(
            <div className="comment">
                <p>Комментарии:</p>
                <div className="list comment">
                {data.map((comment) => (
                    <Comment key={comment.id} comment={comment} id={comment.comment_id}/>
                ))}
                </div> 
            </div>
        )
    }
}
