import { Component } from "react";

export class SignUp extends Component {
    state = {
        login: "",
        password: "",
        name: "",
        errorKey: null,
        errorPlace: null
    }

    UpdateData = (e) => { 
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
    }

    LogButtonClicked = () => {
        const {login, password, name} = this.state
        console.log(`Логин: ${login}`)
        console.log(`Пароль: ${password}`)
        console.log(`Имя игрока: ${name}`)

        if (login === "" && password === "" && name === "") {
            this.setState({errorKey: 0})
            return
        }

        if (login === "") {
            this.setState({errorKey: 1, errorPlace: "логин"})
            return
        }
        if (password === "") {
            this.setState({errorKey: 1, errorPlace: "пароль"})
            return
        }
        if (name === "") {
            this.setState({errorKey: 1, errorPlace: "имя"})
            return
        }

        if (login.startsWith(" ") === true || login.endsWith(" ") === true) {
            this.setState({errorKey: 2, errorPlace: "логина"})
            return
        }
        if (password.startsWith(" ") === true || password.endsWith(" ") === true) {
            this.setState({errorKey: 2, errorPlace: "пароля"})
            return
        }
        if (name.startsWith(" ") === true || name.endsWith(" ") === true) {
            this.setState({errorKey: 2, errorPlace: "имени"})
            return
        }

        this.setState ({
            login: "",
            password: "",
            name: ""
        })
    }

    render () {
        const { login, password, name, errorKey, errorPlace} = this.state
        //switch (errorKey) {
        //    case 0: 
        //}
        return (
            <>
                <label>Регистрация</label>
                <div>
                    <label>логин</label>
                    <input name="login" value={login} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <div>
                    <label>пароль</label>
                    <input type="password" name="password" value={password} onChange={(e) => this.UpdateData(e)}/>
                    <button>Показать</button>
                </div>
                <div>
                    <label>имя игрока/никнейм</label>
                    <input name="name" value={name} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <button onClick={this.LogButtonClicked}>Зарегистироваться</button>
                {errorKey === 0 && <label>Одно или более полей пусты<br></br>
                Пожалуйста, заполните пустые поля!</label>}
                {errorKey === 1 && <label>Поле {errorPlace} оказалось пустым<br></br>
                Пожалуйста, заполните его!</label>}
                {errorKey === 2 && <label>В начале или конце {errorPlace} есть пробелы<br></br>
                Пожалуйста, напишите без них!</label>}
            </>
        )
    }
}
