import { Component } from "react";

export class Forum extends Component {
    render() {
        return(
            <>
                <p>Темы для обсуждения</p>
                <Topics/>
            </>
        )
    }
}

export class Topic extends Component {
    render() {
        const { id, name, description } = this.props;
        return (
        <div className="child-component">
            <a><h3>{name}</h3></a>
        </div>
        );
    }
}

export class Topics extends Component {
    constructor(props) {
        super(props);
        this.state = {
        data: [], // Данные из API.
        loading: true, // Индикатор загрузки.
        error: null, // Ошибки загрузки.
        };
    }

    // Метод жизненного цикла для получения данных после монтирования компонента.
    componentDidMount() {
        /*
        fetchData = async () => {
            try {
                const response = await fetch('https://api.example.com/data'); // Замените URL на ваш API.
                if (!response.ok) {
                    throw new Error("Ошибка загрузки данных");
                }
                const result = await response.json();
                this.setState({ 
                    data: result, loading: false 
                });
            } 
            catch (error) {
                this.setState({ 
                    error: error.message, loading: false 
                });
            }
        }
        */
};

    render() {
        const { data, loading, error } = this.state;

        if (loading) {
            return <p>Загрузка...</p>;
        }

        if (error) {
            return <p>Ошибка: {error}</p>;
        }

        return (
            <>
                <div className="list">
                {data.map((item) => (
                    <Topic key={item.id} {...item} />
                ))}
                </div>
            </>
        );
    }
}
