import { Component } from "react";
import { Link } from "react-router-dom";

export class Topic extends Component {
        componentDidMount() {
            const { location } = this.props;
            if (location && location.state) {
                const { id, name, content, author } = location.state;
                this.setState({
                    ID: id,
                    Title: name,
                    Author: author,
                    Content: content,
                    Loading: false,
                });
            } else {
                this.setState({ Error: "Тема не найдена", Loading: false });
            }
        }

        render() {
            const { Title, Content, Author, Loading, Error } = this.state;

            if (Loading) return <p>Загрузка...</p>;
            if (Error) return <p>{Error}</p>;

            return (
            <div>
                <h1>{Title}</h1>
                <p>Автор: {Author}</p>
                <div>{Content}</div>
            </div>
            );
        }
    }

export class Topics extends Component {
    render() {
        const {data} = this.props
        if (!Array.isArray(data)) {
            return <p>Нет доступных тем</p>;
        }
        return (
            <>
                <div className="list">
                {data.map((topic) => (
                    <div key={topic.id}>
                        <Link to={{
                            pathname: "/topic",
                            state: { 
                                id: topic.id, 
                                name: topic.title, 
                                author: topic.author, 
                                content: topic.content 
                            },
                        }}>
                        </Link>
                    </div>
                ))}
                </div>
            </>
        );
    }
}
