import { Component } from "react";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

export class Forum extends Component {
    render() {
        return(
            <>
                <h1>Темы для обсуждения</h1>
                <button>Создать новое обсуждение</button>
                <Topics/>
            </>
        )
    }
}

export class Topic extends Component {
    render() {
        const { id, name, description } = this.props;
        return (
        <div className="topic">
            <Link to={{
                description: "/topic",
                state : {id, name}
            }}>
                {name}
            </Link>
        </div>
        );
    }
}

export class Topics extends Component {
    constructor(props) {
        super(props);
        this.state = {
        data: [], // Данные из API.
        loading: true, // Индикатор загрузки.
        error: null, // Ошибки загрузки.
        };
    }

    // Метод жизненного цикла для получения данных после монтирования компонента.
    componentDidMount() {
        /*
        fetchData = async () => {
            try {
                const response = await fetch('https://api.example.com/list'); // Замените URL на ваш API.
                if (!response.ok) {
                    throw new Error("Ошибка загрузки данных");
                }
                const result = await response.json();
                this.setState({ 
                    data: result, loading: false 
                });
            } 
            catch (error) {
                this.setState({ 
                    error: error.message, loading: false 
                });
            }
        }
        */
};

    render() {
        const { data, loading, error } = this.state;

        if (loading) {
            return <p>Загрузка...</p>;
        }

        if (error) {
            return <p>Ошибка: {error}</p>;
        }

        return (
            <>
                <div className="list">
                {data.map((item) => (
                    <Topic key={item.id} {...item} />
                ))}
                </div>
            </>
        );
    }
}

export class TopicDis extends Component {
    state = {
        text: [],
        loading: true,
        error: null,
        id: null,
        name: null
    }

    componentDidMount() {
        this.fetchData()
    }
    fetchData = async () => {
        const Location = useLocation()
        const {id, name} = Location.state
        /*
        try {
            const response = await fetch('https://api.example.com/text'); // Замените URL на ваш API.
            if (!response.ok) {
                throw new Error("Ошибка загрузки данных");
            }
            const result = await response.json();
            this.setState({ 
                data: result, 
                loading: false
                id: id
                name: name
            });
        } 
        catch (error) {
            this.setState({ 
                error: error.message, loading: false 
            });
        }
        */
    }


    render() {
        const { text, loading, error, name } = this.state;

        if (loading) {
            return <p>Загрузка...</p>;
        }

        if (error) {
            return <p>Ошибка: {error}</p>;
        }

        return (
            <>
                <h1>{name}</h1>
                <div className="text">
                    {text}
                </div>
            </>
        )
    }
}
