import { Component } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "./Context";
import { Comments } from "./Comments";

export class Forum extends Component {
    state = {
        Topics: [
            {id : 20, name: "first topic", data: "14.12.2024"},
            {id : 27, name: "second topic", data: "14.12.2024"}
        ],
        Loading: false,
        Error: null
    }

    componentDidMount() {
        /*
        fetchData = async () => {
            try {
                const response = await fetch('https://api.example.com/list')
                if (!response.ok) {
                    throw new Error("Ошибка загрузки данных")
                }
                const result = await response.json()
                this.setState({ 
                    Topics: result, 
                    Loading: false 
                });
            } 
            catch (error) {
                this.setState({ 
                    Error: error.message, 
                    Loading: false 
                })
            }
        }
        */
    }

    render() {
        const {Topics, Loading, Error } = this.state;

        if (Loading) {
            return <p>Загрузка...</p>;
        }

        if (Error) {
            return <p>Ошибка: {Error}</p>;
        }

        return (
            <>
                <h1>Темы для обсуждений</h1>
                <Link to="/new_topic"><button>Создать новое обсуждение</button></Link>
                <div className="list">
                <Topics Topics={Topics}/>
                </div>
            </>
        );

    }
}

export class Topic extends Component {
    render() {
        const {topic} = this.props
        return (
        <div className="topic">
            <Link to={{
                pathname: "/topic",
                state : {id: topic.id, name: topic.name}
            }}>
                {topic.name}
            </Link>
            Тема ...
        </div>
        );
    }
}

export class Topics extends Component {
    render() {
        const {Topics} = this.props
        if (!Array.isArray(Topics)) {
            return <p>Нет доступных тем.</p>;
        }
        return (
            <>
                <div className="list">
                {Topics.map((topic) => (
                    <Topic key={topic.id} topic={topic} />
                ))}
                </div>
            </>
        );
    }
}

export class TopicDis extends Component {
    state = {
        // text: [],
        text: "текст",
        loading: false,
        error: null,
        id: null,
        //name: null
        name: "Какая-то тема",
        CommentsList: []
    }

    componentDidMount() {
        this.fetchData()
    }
    fetchData = async () => {
        //const Location = useLocation()
        //const {id, name} = Location.state
        /*
        try {
            const response = await fetch('https://api.example.com/text');
            if (!response.ok) {
                throw new Error("Ошибка загрузки данных")
            }
            const result = await response.json()
            this.setState({ 
                data: result, 
                loading: false,
                id: id,
                name: name
            })
        } 
        catch (error) {
            this.setState({ 
                error: error.message, 
                loading: false 
            })
        }
        */
    }


    render() {
        const { text, loading, error, name, id, CommentsList} = this.state;

        if (loading) {
            return <p>Загрузка...</p>;
        }

        if (error) {
            return <p>Ошибка: {error}</p>;
        }

        return (
            <>
                <h1>{name}</h1>
                <div className="text">
                    {text}
                </div>
                <button>Добавить коментарий</button>
                <Comments data={CommentsList}/>
            </>
        )
    }
}

export class NewTopic extends Component {
    static contextType = AppContext
    state = {
        TopicName: "",
        TopicText: "",
        UserName: this.context.UserName,
        errorKey: null,
    }

    Update = (e) => { 
        const {name, value} = e.target
        this.setState({
            [name]: value,
            errorKey: null
        })
    }

    CreateTopic = () => {
        const {TopicName, TopicText} = this.state
        /*
        try {
            const response = await fetch('https://api.example.com/topics')
            if (!response.ok) {
                throw new Error("Ошибка загрузки данных")
            }
            const result = await response.json()
            this.setState({ 
                data: result, 
                loading: false,
                id: id,
                name: name
            })
        } 
        catch (error) {
            this.setState({ 
                error: error.message, 
                loading: false 
            })
        }
        */

        console.log(`Название обсуждения: ${TopicName}`)

        if (TopicName === "") {
            this.setState({errorKey: 1})
            return
        }
        if (TopicText === "") {
            this.setState({errorKey: 2})
            return
        }

        if (TopicName.startsWith(" ") === true || TopicName.endsWith(" ") === true) {
            this.setState({errorKey: 3})
            return
        }

        this.setState ({
            TopicName: "",
            TopicText: "",
            errorKey: 0
        })
    }

    render() {
        return (
            <>
                <h1>Создание нового обсуждения</h1>
                <div>
                    <p>Название обсуждения</p>
                    <input type="text" name="TopicName" onChange={(e) => this.Update(e)}></input>
                </div>
                <div>
                    <p>Описание</p>
                    <textarea 
                    id="large-text"
                    name="TopicText"
                    onChange={(e) => this.Update(e)}
                    placeholder="Тут можно написать о своих идеях или других предложениях"
                    rows={10} // Высота в строках
                    cols={45} // Ширина в символах
                    style={{
                        width: '100%', // Устанавливает ширину в процентах относительно родителя
                        minHeight: '150px', // Минимальная высота
                        padding: '10px', // Внутренний отступ
                        border: '1px solid #ccc', // Граница
                        borderRadius: '4px', // Закругление углов
                        fontSize: '16px', // Размер шрифта
                        lineHeight: '1.5', // Межстрочный интервал
                        margin: "10px"
                    }}
                    />
                    <button onClick={this.CreateTopic}>Создать</button>
                    {this.state.errorKey === 0 && <p>Новое обсуждение успешно создано!</p>}
                    {this.state.errorKey === 1 && <p>Поле с названием обсуждения оказалось пустым<br></br>
                    Пожалуйста, заполните это поле!</p>}
                    {this.state.errorKey === 2 && <p>Поле с текстом обсуждения оказалось пустым<br></br>
                    Пожалуйста, заполните это поле!</p>}
                    {this.state.errorKey === 3 && <p>В начале или конце названия обсуждения есть пробелы<br></br>
                    Пожалуйста, напишите без них!</p>}
                </div>
            </>
        )
    }
}
