function TopicDis() {
    const location = useLocation();
    const [state, setState] = useState({
        ID: "",
        Title: "",
        Content: "",
        Author: "",
        Error: "",
        Loading: false,
        CommentsList: []
    });

    useEffect(() => {
        if (location.state) {
            const { id, name, content, author } = location.state;
            setState((prevState) => ({
                ...prevState,
                ID: id,
                Title: name,
                Author: author,
                Content: content
            }));
        }
    }, [location.state]);

    const { Title, Content, Author, CommentsList, Loading, Error } = state;

    if (Loading) return <p>Загрузка...</p>;
    if (Error) return <p>Ошибка: {Error}</p>;

    return (
        <>
            <h1>{Title}</h1>
            <p>Автор: {Author}</p>
            <div className="text">{Content}</div>
            <button>Добавить комментарий</button>
            <Comments data={CommentsList} />
        </>
    );
}

export default TopicDis;
