import { Component } from "react";

export class SignUp extends Component {
    state = {
        login: "",
        password: "",
        name: ""
    }

    UpdateData = (e) => {
        const {property, value} = e.target
        this.setState({
            [property]: value
        })
    }

    LogButtonClicked =({clogin, cpassword, cname}) => {
        this.setState({
            login: clogin,
            password: cpassword,
            name: cname
        })
        console.log(this.state.login)
        console.log(this.state.password)
        console.log(this.state.name)

    }

    render () {
        const { login, password, name} = this.state
        return (
            <>
                <div>
                    <label>логин</label>
                    <input name="логин" value={login} onChange={this.UpdateData}/>
                </div>
                <div>
                    <label>пароль</label>
                    <input name="password" value={password}/>
                </div>
                <div>
                    <label>имя игрока/никнейм</label>
                    <input name="name" value={name}/>
                </div>
                <button onClick={this.LoginClick(login, password, name)}>Зарегистироваться</button>
            </>
        )
    }
}
