import { Component } from "react";

export class SignUp extends Component {
    state = {
        login: "kk",
        password: "",
        name: ""
    }

    componentDidMount() {
        console.log('componentDidMount called');
    }

    componentDidUpdate() {
        console.log('componentDidUpdate called');
    }

    UpdateData = (e) => {
        console.log("Update event!")
        console.log(e)
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
    }

    LogButtonClicked = () => {
        console.lon('button clicked!')
        console.log(this.state.login)
        console.log(this.state.password)
        console.log(this.state.name)
        this.setState ({
            login: "",
            password: "",
            name: ""
        })
    }

    render () {
        const { login, password, name} = this.state
        return (
            <>
                <div>
                    <label>логин</label>
                    <input name="login" value={login} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <div>
                    <label>пароль</label>
                    <input type="password" name="password" value={password} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <div>
                    <label>имя игрока/никнейм</label>
                    <input name="name" value={name} onChange={(e) => this.UpdateData(e)}/>
                </div>
                <button onClick={this.LogButtonClicked}>Зарегистироваться</button>
                <label>{login}</label>
            </>
        )
    }
}
